import React from "react";
import withLayout from "../../hoc/WithPageLayout";
import './Contact.css';


const formValidation = () => { 
    var firstName =  
        document.forms["contactForm"]["first-name"]; 
    var lastName =  
        document.forms["contactForm"]["last-name"]; 
    var email =  
        document.forms["contactForm"]["email"]; 
    var phone =  
        document.forms["contactForm"]["phone"]; 
    var comment =  
        document.forms["contactForm"]["comment"]; 

    if (firstName.value === "" && lastName.value === "" && email.value === "" && phone.value === "" && comment.value === "") { 
        alert("Please fill out our contact form."); 
        firstName.focus();
        lastName.focus();
        email.focus();
        phone.focus();
        comment.focus();
        return false; 
    }

    if (firstName.value === "") { 
        window.alert("Please enter your first name."); 
        firstName.focus(); 
        return false; 
    } 

    if (lastName.value === "") { 
        window.alert("Please enter your last name."); 
        lastName.focus(); 
        return false; 
    } 

    if (email.value === "") { 
        window.alert( 
          "Please enter a valid e-mail address."); 
        email.focus(); 
        return false; 
    } 

    if (phone.value === "") { 
        window.alert( 
          "Please enter your phone number."); 
        phone.focus(); 
        return false; 
    } 

    if (comment.value === "") { 
        window.alert("Please enter your message"); 
        comment.focus(); 
        return false; 
    } 
    return true; 
}

